import wave
import subprocess
import os
from pathlib import Path
from keras.models import load_model
import librosa
import numpy as np

# ビデオの長さを返す
def get_video_length(path):
    wr = wave.open(path, 'r')
    fr = wr.getframerate()
    fn = wr.getnframes()
    return 1.0 * fn / fr

# FIX ffmpeg じゃなくて wave モジュールを使う
def cut_video_10sec(path, length):
    os.makedirs("temp")
    for i in range(int(length)):
        outputPath = './temp/{1:05}.wav'.format(i)
        cmd = 'ffmpeg -loglevel error -i "{0}" -ss {1} -t 10 -acodec copy "{2}"'.format(path, i*10, outputPath)
        subprocess.run(cmd, shell=True)

# 指定したディレクトリにあるwavファイルのリストを返す
def get_wav_filelist(dirname = "."):
    _list = Path(dirname).glob("./*.wav")
    _list = list(_list)
    _list.sort()
    return _list

# メル周波数スペクトラムを返す
def calculate_melsp(x, n_fft=1024, hop_length=128):
    stft = np.abs(librosa.stft(x, n_fft=n_fft, hop_length=hop_length))**2
    log_stft = librosa.power_to_db(stft)
    melsp = librosa.feature.melspectrogram(S=log_stft,n_mels=128)
    return melsp

def format_wav_cnn(filename, aug=None, rates=None):
    _x, fs = librosa.load(filename, sr=48000, duration=9.9)
    _x = calculate_melsp(_x)
    _x = _x[None, ..., None]
    return _x

# 学習済みモデルを利用して，判定結果が入った配列を返す
def validate_song_cnn(fileList):
    freq          = 128
    time          = 3713
    song          = 0
    not_song      = 1
    model = load_model("model_20epochs_cnn.h5")
    songFlag = [0] * len(fileList)
    for i in range(len(fileList)):
        fileName = fileList[i]
        ret = model.predict(format_wav_cnn(fileName))
        # 該当部分がsongなら1を入れる
        if ret[0][0] > 0.5:
            songFlag[i] = 1
        else:
            songFlag[i] = 0
        # print("{} => {} [ {:.2%}, {:.2%} ]".format(fileList[i], songFlag[i], ret[0][0], ret[0][1]))
    return songFlag

# 判定結果が入った配列を利用して，曲の開始・終了が入った配列を返す
def get_songs(songFlag):
    flag = 0
    songStart = 0
    res = []
    for i in range(len(songFlag)):
        s = songFlag[i]
        if s==1 and flag==0:
            songStart = i
            flag += 1
        elif s==1:
            flag += 1
        elif s==0 and flag>=3:
            res.append((songStart, i-1))
            flag = 0
        else:
            flag = 0
    if flag >= 3:
        res.append((songStart, len(songFlag)-1))
    return res

# 曲の開始・終了が入った配列から，m4a?ファイルにして出力する
def output_songs(path, songs):
    os.makedirs("output", exist_ok=True)
    for i in range(len(songs)):
        outputPath = './output/{:04}.wav'.format(i)
        start = songs[i][0] * 10
        length = songs[i][1] * 10 - start
        cmd = 'ffmpeg -loglevel error -i "{0}" -ss {1} -t {2} -acodec copy "{3}"'.format(path, start, length, outputPath)
        subprocess.run(cmd, shell=True)


